sys_script.script:
(function executeRule(current, previous /*null when async*/) {

//evaluation administrator group
	var newEvalAdmin = new GlideRecord('sys_user_group');
	newEvalAdmin.name = current.number+"_eval_admin";
	newEvalAdmin.description = "Auto-generated role group for the Solution Evaluation Tool.";
	newEvalAdmin.manager = current.administrator.sys_id;
	newEvalAdmin.insert();
	//return sys ID from glide record group creation

	var evalAdmin = new GlideRecord('sys_group_has_role');
	evalAdmin.group = '';
	//need sys id of group from above
	evalAdmin.role = 'x_inthe_eval.eval_admin';
	evalAdmin.inherits = true;


//additional evaluators group
	var newAddEvals = new GlideRecord('sys_user_group');
	newAddEvals.name = current.number+"_evaluators";
	newAddEvals.description = "Auto-generated role group for the Solution Evaluation Tool.";
	newAddEvals.manager = current.administrator.sys_id;
	newAddEvals.insert();
	//return sys ID from glide record group creation

	var addEvalsRole = new GlideRecord('sys_group_has_role');
	addEvalsRole.group = '';
	//need sys id of group from above
	addEvalsRole.role = 'x_inthe_eval.evaluator';
	addEvalsRole.inherits = true;


//evaluation users group
	var newUsers = new GlideRecord('sys_user_group');
	newUsers.name = current.number+"_users";
	newUsers.description = "Auto-generated role group for the Solution Evaluation Tool.";
	newUsers.manager = current.administrator.sys_id;
	newUsers.insert();
	//return sys ID from glide record group creation

	var newUsersRole = new GlideRecord('sys_group_has_role');
	newUsersRole.group = '';
	//need sys id of group from above
	newUsersRole.role = 'x_inthe_eval.user';
	newUsersRole.inherits = true;

})(current, previous);
