sys_script_client.script:
function onLoad() {
   //Gives the user an explanation of how to use the form and/or make it editable.
  	var state = g_form.getValue('state');
	if(state == 'in_progress'||state == 'new'){
	g_form.addInfoMessage("Please use the list below to mark your ratings for this solution. When all your ratings have been marked, change the state to Final and save.");
	}
	if(state =='complete'){
		g_form.addErrorMessage("You cannot edit this Score Card because it is marked as "+ state + ".");
	}
	if(state =='final'){
		g_form.addInfoMessage("As your scores are changed, they will be marked as final and locked. To change final scores, you must change the score card's state to In Progress and save.");
	} 
}
