sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var ucLeadSysID = '';
	var ucEvalSysID = '';
	var parentSysID = '';
	parentSysID = current.evaluation.role_group;
	//gs.addInfoMessage('Parent sys ID is ' +parentSysID);

//use case lead group
	var newUCLead = new GlideRecord('sys_user_group');
	newUCLead.initialize();
	newUCLead.name = current.number+"_uc_lead";
	newUCLead.description =  "DO NOT DELETE. Auto-generated role group for Solution Evaluation "+current.number;
	newUCLead.manager = current.evaluation.administrator;
	newUCLead.parent = parentSysID;
	newUCLead.insert();
	ucLeadSysID = newUCLead.sys_id;

	var UCLeadRole = new GlideRecord('sys_group_has_role');
	UCLeadRole.group = ucLeadSysID;
	UCLeadRole.role = '1ae3f1074fef3e40af76d0af0310c76b';
	UCLeadRole.inherits = true;

//use case evaluators group
	var newUCevals = new GlideRecord('sys_user_group');
	newUCevals.name = current.number+"_evaluators";
	newUCevals.description =  "DO NOT DELETE. Auto-generated role group for Solution Evaluation "+current.number;
	newUCevals.manager = current.evaluation.administrator;
	newUCevals.parent = parentSysID;
	newUCevals.insert();
	ucEvalSysID = newUCevals.sys_id;

	var newUCevalsRole = new GlideRecord('sys_group_has_role');
	newUCevalsRole.group = ucEvalSysID;
	newUCevalsRole.role = '609c45cb4f6f3e40af76d0af0310c73a';
	newUCevalsRole.inherits = true;
	newUCevalsRole.insert();

})(current, previous);
