sys_script.script:
(function executeRule(current, previous /*null when async*/) {
//use case lead group
	var newUCLead = new GlideRecord('sys_user_group');
	newUCLead.name = current.number+"_uc_lead";
	newUCLead.description = "Auto-generated role group for the Solution Evaluation Tool.";
	newUCLead.manager = current.evaluation.administrator.sys_id;
	newUCLead.insert();
	//return sys ID from glide record group creation

	var UCLeadRole = new GlideRecord('sys_group_has_role');
	UCLeadRole.group = '';
	//need sys id of group from above
	UCLeadRole.role = 'x_inthe_eval.uc_lead';
	UCLeadRole.inherits = true;

//use case evaluators group
	var newUCevals = new GlideRecord('sys_user_group');
	newUCevals.name = current.number+"_evaluators";
	newUCevals.description = "Auto-generated role group for the Solution Evaluation Tool.";
	newUCevals.manager = current.evaluation.administrator.sys_id;
	newUCevals.insert();
	//return sys ID from glide record group creation

	var newUCevalsRole = new GlideRecord('sys_group_has_role');
	newUCevalsRole.group = '';
	//need sys id of group from above
	newUCevalsRole.role = 'x_inthe_eval.evaluator';
	newUCevalsRole.inherits = true;

})(current, previous);
