sys_ui_action.script:
//check child ratings

//changes child criteria state to complete

//changes child use cases to complete

//changes evaluation state to complete

//SOLUTIONS COUNTS
	//variables and arrays
		//get array of use cases - j
		var useCaseArray = [];
		grUseCase = new GlideRecord('x_inthe_eval_use_case');
		grUseCase.addQuery('evaluation',current.sys_id);
		grUseCase.query();
		while (grUseCase.next()){
			useCaseArray.push(grUseCase.sys_id);
		}
		gs.addInfoMessage("Use Case array is: "+useCaseArray.toString());

		//get array of solutions - k
		var solutionsArray = [];
		grSolutions = new GlideRecord('x_inthe_eval_solution');
		grSolutions.addQuery('evaluation',current.sys_id);
		grSolutions.query();
		while (grSolutions.next()){
			solutionsArray.push(grSolutions.sys_id);
		}
		gs.addInfoMessage("Solution array is: "+solutionsArray.toString());


	//counts ratings
	var ucTotalArray =[];
	var ucTotal = new GlideRecord('x_inthe_eval_rating');
	ucTotal.addQuery('use_case.evaluation',current.sys_id);
	for (k=0;k<solutionsArray.length;k++){
		ucTotal.addQuery('solution',solutionsArray[k]);
	}
	ucTotal.query();
	while (ucTotal.next()){
		ucTotalArray.push(ucTotal.sys_id);
		var ucTotalCount = ucTotalArray.getRowCount();
	}
gs.addInfoMessage("Use Case array is: " + ucTotal.toString());
gs.addInfoMessage("Use Case Total Count is: "+ucTotalCount);



	//counts not rated

	//counts not met

	//counts essential not met

	//counts fully met

	//counts partially met

	//counts essential partially met

	//counts essential fully met

	//adds counts for total variables


//sets form values (percentages)
