sys_ui_action.script:
//Validate child criterion are marked as final
var criStateArray = [];
var grCri = new GlideRecord('x_inthe_eval_criterion');
grCri.addQuery('use_case',current.sys_id);
grCri.query();
while (grCri.next()) { 
	criStateArray.push(grCri.state+'');}
var criteriaStates = criStateArray.toString();
var notFinal = criteriaStates.includes("in_progress" || 'new');
if(notFinal == true){
	gs.addErrorMessage("Not all child Criteria were marked as Final. Please review and finalize each Criteria.");
	}
else if (notFinal == false){

	//Mark current use case as Final
	var grCurrent = new GlideRecord('x_inthe_eval_use_case');
	grCurrent.addQuery('sys_id',current.sys_id);
	grCurrent.query();
	grCurrent.setValue('state','final');
	grCurrent.updateMultiple();	
}
action.setRedirectURL(current);
