sys_ui_action.script:
//Validate child use cases are marked as final
var ucStateArray = [];
var grUC = new GlideRecord('x_inthe_eval_use_case');
grUC.addQuery('evaluation',current.sys_id);
grUC.query();
var ucCount = grUC.getRowCount();
while (grUC.next()) { 
	ucStateArray.push(grUC.state+'');}
var ucStates = ucStateArray.toString();
//gs.addInfoMessage("UC state array: " +ucStates);
var notFinal = ucStates.includes("in_progress" || 'new');
if(notFinal == true){
	gs.addErrorMessage("Use Cases have not been marked as Final. Please review and finalize each Use Case and Criteria before finalizing the Evaluation.");
	}
else if (notFinal == false|| ucCount > 0){

	//Mark child Solutions as Final
	var grSol = new GlideRecord('x_inthe_eval_solution');
	grSol.addQuery('evaluation', current.sys_id);
	grSol.query();
	var solCount = grSol.getRowCount();
	if (solCount == 0){
		gs.addErrorMessage("There are no Solutions created for this Evaluation. Please create a solution before continuing.");
	}
	else if (ucCount > 0){
	grSol.setValue('state', 'final');
	grSol.updateMultiple();
	gs.addInfoMessage("Criteria, Use Cases, and Solutions are finalized and ready for you to generate Score Cards.");

	//Mark current evaluation as Final
	var grCurrent = new GlideRecord('x_inthe_eval_evaluation');
	grCurrent.addQuery('sys_id',current.sys_id);
	grCurrent.query();
	grCurrent.setValue('state','final');
	grCurrent.updateMultiple();	
	}
}
action.setRedirectURL(current);
